css:
.print-format {
  font-family: 'Helvetica Neue', sans-serif;
  font-size: 10pt;
}
.header-section {
  text-align: center;
  margin-bottom: 15px;
  border-bottom: 2px solid #333;
  padding-bottom: 10px;
}
.company-name {
  font-size: 14pt;
  font-weight: bold;
}
.receipt-title {
  font-size: 12pt;
  margin-top: 5px;
}
.info-table {
  width: 100%;
  margin-bottom: 10px;
}
.info-table td {
  padding: 3px 0;
}
.info-label {
  font-weight: bold;
  width: 40%;
}
.qr-section {
  text-align: center;
  margin: 15px 0;
}
.qr-code {
  max-width: 150px;
  height: auto;
}
.lottery-section {
  text-align: center;
  font-size: 14pt;
  font-weight: bold;
  margin: 10px 0;
  padding: 10px;
  border: 2px dashed #333;
}
.amount-section {
  text-align: right;
  font-size: 12pt;
}
.total-amount {
  font-size: 16pt;
  font-weight: bold;
}
